sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ((isLoading && !g_form.isNewRecord()&& g_form.getValue('u_request_for_email')!='') || (newValue === '')||(newValue==oldValue) ) {
      return;
   }


   //create GlideAjax object
	var getEmail=new GlideAjax('GetEmailAddress');
	//add method and parameter 
	getEmail.addParam('sysparm_name','getEmail');
	getEmail.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	//call server script include to execute method passed using glide ajax and call callback, when get response
	getEmail.getXML(populateEmail);

	function populateEmail(response){
		var emailFromScriptInclude=response.responseXML.documentElement.getAttribute('answer');
		g_form.clearValue('u_request_for_email');
		g_form.setValue('u_request_for_email',emailFromScriptInclude);
	}

}
