sys_script_include.script:
var GetNumberAndShortDescriptionOfIncidentRecord = Class.create();
GetNumberAndShortDescriptionOfIncidentRecord.prototype = {
    initialize: function() {
    },
	getSubIncidentRecordList: function(tableName,limit){
		var subIncList=[];
		var incGr=new GlideRecord(tableName);
		incGr.setLimit(limit);
		incGr.query();

		while(incGr.next()){
			var item={};
			item.number=incGr.getValue('number');
			item.shortdesc=incGr.getValue('short_description');
			subIncList.push(item);
		}
		return subIncList;
	},

    type: 'GetNumberAndShortDescriptionOfIncidentRecord'
};
