sysauto_script.condition:
var answer=true;
var gdt=new GlideDateTime();
var day=gdt.getDayOfMonthLocalTime();

if(day==6||day==7){
	answer=false;
}
answer;

sysauto_script.script:
var NITask=new GlideRecord('x_58872_needit_needit_task');

NITask.addQuery('due_date','>',gdt);
NITask.addQuery('due_date','<=',gs.hoursAgo(-24));
NITask.addQuery('state','!=',3);
NITask.query();

while(NITask.next()){
	gs.info(NITask.getValue('number')+' '+NITask.getValue('short_description'));
	gs.eventQueue('x_58872_needit.MakeDueSoon24TaskPriority',NITask, NITask.getValue('number'),gs.getUserName());
}
